class Auth {
    constructor() {
      this.authenticated = sessionStorage.getItem("authenticatedUser");
    }

    login(cb) {
      sessionStorage["authenticatedUser"]=true;
      cb();
    }

    logout(cb) {
      sessionStorage["authenticatedUser"]=false;
      cb();
    }

    isAuthenticated() {
      return this.authenticated;
    }
  }

  export default new Auth();
