class timetableController {
    constructor() {
        this.state = {
            reloadTable: null,
            redirect: null,
            myUser: null,
            userId: sessionStorage.getItem("userId"),
            tableId: null,
            periodLenght: 30,
            monday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            tuesday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            wednesday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            thursday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            friday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            mondayDDBB: null,
            tuesdayDDBB: null,
            wednesdayDDBB: null,
            thursdayDDB: null,
            fridayDDBB: null,
            aux: ""
        }
    }

    handleChange = async (time, day, dayData) => {
        if (time == null) return;

        const filteredTime = await String(time).match(/\d{2}:\d{2}/g)[0];
        switch (day) {
            case "mondayFrom":
                dayData.myDateFrom = time;
                dayData.from = filteredTime;
                return dayData;

            case "mondayTo":
                this.state.monday.myDateTo = time;
                this.state.monday.to = filteredTime;
                return this.state.monday;
            case "tuesdayFrom":
                this.state.tuesday.myDateFrom = time;
                this.state.tuesday.from = filteredTime;
                break;
            case "tuesdayTo":
                this.state.tuesday.myDateTo = time;
                this.state.tuesday.to = filteredTime;
                break;
            case "wednesdayFrom":
                this.state.wednesday.myDateFrom = time;
                this.state.wednesday.from = filteredTime;
                break;
            case "wednesdayTo":
                this.state.wednesday.myDateTo = time;
                this.state.wednesday.to = filteredTime;
                break;
            case "thursdayFrom":
                this.state.thursday.myDateFrom = time;
                this.state.thursday.from = filteredTime;
                break;
            case "thursdayTo":
                this.state.thursday.myDateTo = time;
                this.state.thursday.to = filteredTime;
                break;
            case "fridayFrom":
                this.state.friday.myDateFrom = time;
                this.state.friday.from = filteredTime;
                break;
            case "fridayTo":
                this.state.friday.myDateTo = time;
                this.state.friday.to = filteredTime;
                break;

            default:
                break;
        }
        //This is just to render the changes
        //.setState({ aux: ":)" })
    };

  }

  export default new timetableController();
