import React, { Component, useState } from "react";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

class App extends Component {
    state = {
        monday: {
            myDateFrom: new Date(),
            from: "",
            myDateTo: new Date(),
            to: ""
        }
    };

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-lg-9">
                        <div className="card">
                            <div className="card-body">
                                <DatePicker
                                    selected={()=> {
                                        console.log(this.state.monday.myDateFrom);
                                        this.state.monday.myDateFrom;
                                    }}
                                    onChange={time => this.handleChange(time, 'mondayFrom')}
                                    showTimeSelect
                                    showTimeSelectOnly
                                    timeIntervals={20}
                                    timeCaption="Time"
                                    dateFormat="h:mm aa"
                                />

                                <DatePicker
                                    selected={this.state.monday.myDateTo}
                                    onChange={time => this.handleChange(time, 'mondayTo')}
                                    showTimeSelect
                                    showTimeSelectOnly
                                    timeIntervals={20}
                                    timeCaption="Time"
                                    dateFormat="h:mm aa"
                                />
                            </div>
                        </div>
                    </div>
                </div>
</div>
        );
    }

    handleChange = async (time,day) => {
        console.log(new Date());
        console.log(time);
        const filteredTime = await String(time).match(/\d{2}:\d{2}/g)[0];
        switch (day) {
            case "mondayFrom":
                this.state.monday.myDateFrom = time;
                this.state.monday.from = filteredTime;
                break;
            case "mondayTo":
                this.state.monday.myDateTo = time;
                this.state.monday.to = filteredTime;
                break;

            default:
                break;
        }
        console.log(this.state.monday);
    };

}

export default App;
