class Auth {
    constructor() {
      sessionStorage["authenticatedUser"]=false;
    }

    login(cb) {
      sessionStorage["authenticatedUser"]=true;
      cb();
    }

    logout(cb) {
      sessionStorage["authenticatedUser"]=false;
      cb();
    }

    isAuthenticated() {
      return sessionStorage.getItem("authenticatedUser");
    }
  }

  export default new Auth();
