class timetableController {
    constructor() {
        this.state = {
            reloadTable: null,
            redirect: null,
            myUser: null,
            userId: sessionStorage.getItem("userId"),
            tableId: null,
            periodLenght: 30,
            monday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            tuesday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            wednesday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            thursday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            friday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            mondayDDBB: null,
            tuesdayDDBB: null,
            wednesdayDDBB: null,
            thursdayDDB: null,
            fridayDDBB: null,
            aux: ""
        }
    }

    handleChange = async (time, day, dayData) => {
        if (time == null) return;
        const filteredTime = await String(time).match(/\d{2}:\d{2}/g)[0];
        if (day == "from") {
            dayData.myDateFrom = time;
            dayData.from = filteredTime;
        } else if (day == "to") {
            dayData.myDateTo = time;
            dayData.to = filteredTime;
        }

        return dayData;
    }

}

export default new timetableController();
