import React, { Component, useState } from "react";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

class App extends Component {
    state = {
        startDate: new Date(),
        monday: {
            from: "",
            to: ""
        }
    };

    render() {
        return (
            <div>
            <DatePicker
                selected={this.state.startDate}
                onChange={date => this.handleChange(date,"mondayFrom")}
                showTimeSelect
                showTimeSelectOnly
                timeIntervals={20}
                timeCaption="Time"
                dateFormat="h:mm aa"
            />
            <DatePicker
                selected={this.state.startDate}
                onChange={date => this.handleChange(date,"mondayTo")}
                showTimeSelect
                showTimeSelectOnly
                timeIntervals={20}
                timeCaption="Time"
                dateFormat="h:mm aa"
            />
            </div>
        );
    }

    handleChange = async (time,day) => {
        switch (day) {
            case "mondayFrom":
                await this.setState(monday.from =  String(time).match(/\d{2}:\d{2}/g)[0]);
                break;
            case "mondayTo":
                await this.setState(monday.to =  String(time).match(/\d{2}:\d{2}/g)[0]);
                break;

            default:
                break;
        }
        console.log(JSON.stringify(this.state.monday));
    };

}

export default App;
