import React, { Component, useState } from "react";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

class App extends Component {
    state = {
        monday: {
            myDateFrom: new Date(),
            from: "",
            myDateTo: new Date(),
            to: ""
        },
        aux: ""
    };

    render() {
        return (
            <div className="container">
                <div className="row my-5">
                    <div className="col-lg-12">
                        <table class="table table-bordered table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Lunes</th>
                                    <th scope="col">Martes</th>
                                    <th scope="col">Miércoles</th>
                                    <th scope="col">Jueves</th>
                                    <th scope="col">Viernes</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td id="mondayRow"></td>
                                    <td id="tuesdayRow"></td>
                                    <td id="wednesdayRow"></td>
                                    <td id="thursdayRow"></td>
                                    <td id="fridayRow"></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
                <div className="row">
                    <div className="col-lg-9">
                        <div className="card">
                            <div className="card-body">
                                <div class="container">
                                    <div class="row">
                                        <div class="col-sm">
                                            <h4>Lunes</h4>
                                        </div>
                                        <div class="col-sm">
                                            <h6 className="float-left">Desde</h6>
                                            <DatePicker
                                                selected={this.state.monday.myDateFrom}
                                                onChange={time => this.handleChange(time, 'mondayFrom')}
                                                showTimeSelect
                                                showTimeSelectOnly
                                                timeIntervals={20}
                                                timeCaption="Time"
                                                dateFormat="h:mm aa"
                                            />
                                        </div>
                                        <div class="col-sm">
                                            <h6 className="float-left">Hasta</h6>
                                            <DatePicker
                                                selected={this.state.monday.myDateTo}
                                                onChange={time => this.handleChange(time, 'mondayTo')}
                                                showTimeSelect
                                                showTimeSelectOnly
                                                timeIntervals={20}
                                                timeCaption="Time"
                                                dateFormat="h:mm aa"
                                            />
                                        </div>
                                        <div class="col-sm">
                                            <button type="button" class="btn btn-primary m-1">Añadir</button>
                                            <button type="button" class="btn btn-danger m-1">Vaciar</button>
                                        </div>
                                    </div>
                                </div>



                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    handleChange = async (time, day) => {
        const filteredTime = await String(time).match(/\d{2}:\d{2}/g)[0];
        switch (day) {
            case "mondayFrom":
                this.state.monday.myDateFrom = time;
                this.state.monday.from = filteredTime;
                break;
            case "mondayTo":
                this.state.monday.myDateTo = time;
                this.state.monday.to = filteredTime;
                break;

            default:
                break;
        }
        //This is just to render the changes
        this.setState({ aux: ":)" })
    };

}

export default App;
