import ProfessionalService from '../services/professional.service';
import TimetableService from '../services/timetable.service';
import Swal from 'sweetalert2';

class timetableController {
    constructor() {
        this.state = {
            myUser: null,
            userId: sessionStorage.getItem("userId"),
            tableId: null,
            periodLenght: 30,
            monday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            tuesday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            wednesday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            thursday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            friday: {
                myDateFrom: null,
                from: "",
                myDateTo: null,
                to: ""
            },
            mondayDDBB: null,
            tuesdayDDBB: null,
            wednesdayDDBB: null,
            thursdayDDB: null,
            fridayDDBB: null
        }
    }

    handleChange = async (time, day, dayData) => {
        if (time == null) return;
        const filteredTime = await String(time).match(/\d{2}:\d{2}/g)[0];
        if (day == "from") {
            dayData.myDateFrom = time;
            dayData.from = filteredTime;
        } else if (day == "to") {
            dayData.myDateTo = time;
            dayData.to = filteredTime;
        }
        return dayData;
    }

    async updateTimetable(week, myUser) {
        let updated = false;
        if (week.id == null) {
            await TimetableService.create(week).then((res) => {
                myUser.timetableId = res.data.id;
                ProfessionalService.update(myUser).then(() => {
                    Swal.fire({
                        toast: true,
                        title: "Horario actualizado",
                        position: 'top-end',
                        icon: 'success',
                        showConfirmButton: false,
                        timer: 2000
                    });
                    updated = true;
                    //this.setState({ reloadTable: true })
                }).catch((error) => console.error("error creating timetable " + error));

            }).catch((error) => console.error("error creating timetable " + error))
        } else {
            await TimetableService.update(week).then((res) => {
                if (res.data.success == true) {
                    //this.setState({ reloadTable: true })
                    Swal.fire({
                        toast: true,
                        title: "Horario actualizado",
                        position: 'top-end',
                        icon: 'success',
                        showConfirmButton: false,
                        timer: 2000
                    });
                    updated = true;
                }
            }).catch((error) => console.error("error actualizando timetable " + error));
        }
        return updated;
    }

}

export default new timetableController();
