                this.state.wednesday.from = filteredTime;
                break;
            case "wednesdayTo":
                this.state.wednesday.myDateTo = time;
                this.state.wednesday.to = filteredTime;
                break;
            case "thursdayFrom":
                this.state.thursday.myDateFrom = time;
                this.state.thursday.from = filteredTime;
                break;
