import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

import ReactDOM from 'react-dom';
import { GoogleLogin } from 'react-google-login';

class signinComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {};
    }

    responseGoogle = (response) => {
        console.log(response);
        //TODO COMPROBAR QUE EL INICIO HAYA SIDO CORRECTO
        sessionStorage["userName"] = response.profileObject.name;
        sessionStorage["userEmail"] = response.profileObject.email;
        sessionStorage["userUrl"] = response.profileObject.imageUrl;

        this.props.history.push('/crear-perfil'); 
    }

    render() {
        return (
            <div class="container p-4">
                <div class="row">
                    <div class="col-md-4 mx-auto">
                        <div class="card text-center">
                            <div class="card-header">
                                <h1>Iniciar Sesi√≥n</h1>
                            </div>
                            <div class="card-body">
                                <GoogleLogin
                                    className = "btn btn-warning"
                                    clientId="820637070016-genrk31ge28bjg97du1q9bkvsa0p6bdq.apps.googleusercontent.com"
                                    render={renderProps => (
                                        <button onClick={renderProps.onClick}>Iniciar sesion</button>
                                    )}
                                    buttonText="Login"
                                    onSuccess={this.responseGoogle}
                                    onFailure={this.responseGoogle}
                                    cookiePolicy={'single_host_origin'}
                                />

                            </div>
                        </div>
                    </div>
                </div>
            </div>

        );
    }
}

export default signinComponent;
